// Get input directly
const items = $input.all();
const result = [];

// Debug info
const debug = {
  inputType: typeof items,
  inputLength: items ? items.length : 0,
  firstItem: items && items.length > 0 ? JSON.stringify(items[0]) : 'none'
};

// Try to process input
try {
  if (items && items.length > 0) {
    const item = items[0];

    if (item && item.json && item.json.keywords) {
      // Split keywords by comma
      const keywords = item.json.keywords.split(',').map(k => k.trim());
      const pageCount = item.json.page || 1;

      // Generate combinations
      for (const keyword of keywords) {
        for (let page = 1; page <= pageCount; page++) {
          result.push({
            keywords: keyword,
            page: page
          });
        }
      }
    }
  }

  debug.result = result;
  return result.length > 0 ? result : debug;
} catch (error) {
  debug.error = error.message;
  return debug;
}

const filteredPosts = [];

for (const item of $input.all()) {
  const posts = item.json?.data?.items || [];

  for (const post of posts) {
    if (post.reposted) continue;

    const insights = post.socialActivityCountsInsight || {};
    const totalReactions = insights.totalReactionCount || 0;
    const numComments = insights.numComments || 0;
    const textLength = (post.text || '').length;

    if (totalReactions > 30 && numComments > 10 && textLength > 350) {
      const author = post.author || {};

      filteredPosts.push({
        urn: post.urn,
        url: post.url,
        text: post.text,
        postedDate: post.postedDate,
        postedDateTimestamp: post.postedDateTimestamp,
        fullName: author.fullName,
        username: author.username,
        headline: author.headline,
        numComments: insights.numComments,
        totalReactionCount: insights.totalReactionCount
      });
    }
  }
}

return filteredPosts;

