const items = $input.all();

// Extract title from input (assumes it's present in the first item)
const title = items[0].json.title || "LinkedIn Sports Engagement Summary";

const emailSections = items.map(item => {
  const data = item.json;

  return `
    <div style="margin-bottom: 24px; padding: 12px; border-bottom: 1px solid #ddd;">
      <h3 style="margin: 0 0 8px 0;"><strong>${data.postTitle || "Post"}</strong></h3>
      <p>🧑‍🎨 <i>${data["full name"] || data.fullName || "Author"}</i></p>
      <p><strong>📝 Summary:</strong><br>${data.summary}</p>
      <p><strong>💬 Suggested Comment:</strong><br>${data.commentSuggestion || data.commentToPost}</p>
      <p><a href="${data.url}" target="_blank">🔗 View Post</a></p>
    </div>
  `;
});

const htmlBody = `
  <html>
    <head>
      <style>
        body {
          font-family: Arial, sans-serif;
          color: #333;
        }
      </style>
    </head>
    <body>
      <h2 style="color: #222;"><strong>${title}</strong></h2>
      ${emailSections.join("\n")}
    </body>
  </html>
`;

return [
  {
    json: {
      subject: `🔥 ${title}`,
      body: htmlBody,
      bodyFormat: "html"
    }
  }
];

